package main

import (
		"strconv"
	_ "net/http/pprof"
	"github.com/pkg/profile"
)

func main(){
	defer profile.Start().Stop()
	n := ""
	k := 9
	sum := n
	sum = getSum(sum)
	sum = concatenateKTimes(sum,k)
	sum = getSum(sum)
	println(sum)
}

func getSum(sum string) string {
	for len(sum) > 1 {
		sum = sumLetters(sum)
	}
	return sum
}

func sumLetters(digitString string) string {
	sum := 0
	for _,digitChar := range digitString {
		digit, err := strconv.Atoi(string(digitChar))
		if err != nil {
			panic(err)
		}
		sum += digit
	}
	return strconv.Itoa(sum)
}

func concatenateKTimes(digitString string, k int) string {
	concatenatedString := digitString
	for i:= 0; i < k-1; i++ {
		concatenatedString += digitString
	}
	return concatenatedString
}